pronunAns = "";
var store = window.localStorage;
if (store.pronunIn == undefined)
    store.pronunIn = "";
function check(d) {
    for (var j = d * 30; j < (d + 1) * 30; j++)
        if (store.pronunIn[j] != pronunAns[j])
            return false;
    return true;
}
function fill(d) {
    var s = document.getElementsByClassName("btn-grp");
    for (var i = 0; i < s.length; i++) {
        var t = document.getElementsByName("d" + d + "_t" + (i + 1));
        for (var j = 0; j < t.length; j++) {
            if (t[j].value == store.pronunIn[(d - 1) * 30 + i])
                t[j].checked = true;
        }
    }
}
const replaceStr = (str, index, char) => {
    return str.substring(0, index) + char + str.substring(index + 1);
}
function submit(d) {
    var s = document.getElementsByClassName("btn-grp");
    for (var i = 0; i < s.length; i++) {
        var t = document.getElementsByName("d" + d + "_t" + (i + 1));
        for (var j = 0; j < t.length; j++) {
            if (t[j].checked) {
                store.pronunIn = replaceStr(store.pronunIn, (d - 1) * 30 + i, t[j].value);
            }
        }
    }
    window.location.href = "day" + d + "_ans.html";
}
function judge(d) {
    var tmp = document.getElementsByClassName("border");
    for(var i = 1;i <= tmp.length;i++)
    {
        var s = document.getElementById(i);
        if(store.pronunIn[(d-1)*30+i-1] != pronunAns[(d-1)*30+i-1] )
            s.className='WA';
    }
}
function back(d) {
    window.location.href = "../pronun.html";
}